/**
 * popular顶部六个路由
 */
import {Link} from 'react-router-dom';
import {useState} from 'react';

const PopularNav = ({callback}) => {
    const [pageType, setPageType] = useState(undefined);


    const typeList = [
        {
            label: 'All',
            type: "all",
        },
        {
            label: 'Javascript',
            type: '+language:javascript',
        },
        {
            label: 'Ruby',
            type: '+language:Ruby',
        },
        {
            label: 'Java',
            type: '+language:Java',
        },
        {
            label: 'CSS',
            type: '+language:CSS',
        },
    ];

    return (
        <div style={{
            flexDirection: 'row',
            alignItems: 'center',
            display: 'flex',
            listStyle: 'none',
            fontWeight: 'bold',
        }}
        >
            {
                typeList.map((i, index) => (
                    // eslint-disable-next-line jsx-a11y/no-static-element-interactions
                    <li onClick={() => {
                        window.location.href=`/popular?type=${i.type}`;
                        setPageType(i.type);
                        callback && callback(i.type)
                    }} key={index}>
                        <Link
                            style={{margin: '10px 10px', textDecoration: 'none', fontSize: '16px'}}
                            // to={{
                            //     pathname: '/popular?type=ll',
                            //     type: i.type,
                            // }}
                            // to='/popular/?type=ll'
                            className={(pageType === i.type) ? 'topNavBtnSelect' : 'topNavBtn'}
                            replace
                        >
                            {i.label}
                        </Link>
                    </li>

                ))
            }

        </div>
    );
};
export default PopularNav;
