/**
 * popular顶部六个路由
 */
import {useState} from 'react';

// eslint-disable-next-line no-unused-vars
const PopularNav = ({callback}) => {
    const [pageType1, setPageType1] = useState("all");


    const typeList = [
        {
            label: 'All',
            type: "all",
        },
        {
            label: 'Javascript',
            type: '+language:javascript',
        },
        {
            label: 'Ruby',
            type: '+language:Ruby',
        },
        {
            label: 'Java',
            type: '+language:Java',
        },
        {
            label: 'CSS',
            type: '+language:CSS',
        },
    ];

    return (
        <div style={{
            flexDirection: 'row',
            alignItems: 'center',
            display: 'flex',
            listStyle: 'none',
            fontWeight: 'bold',
        }}
        >
            {
                typeList.map((i, index) => (
                    // eslint-disable-next-line jsx-a11y/no-static-element-interactions
                    <a onClick={() => {
                        setPageType1(i.type);
                        alert(i.type)
                        // callback && callback(i.type)
                        window.location.href=`/popular?type=${i.type}`;
                    }}
                        key={index}
                    >
                        <div
                            style={{margin: '10px 10px', textDecoration: 'none', fontSize: '16px'}}
                            // to={{
                            //     pathname: '/popular?type=ll',
                            //     type: i.type,
                            // }}
                            // to='/popular/?type=ll'
                            className={(pageType1 === i.type) ? 'topNavBtnSelect' : 'topNavBtn'}
                        >
                            { pageType1 === i.type ? i.label : "777"}
                        </div>
                    </a>

                ))
            }

        </div>
    );
};
export default PopularNav;
