/**
 * popular顶部六个路由
 */
import {useState} from 'react';
import {Link} from "react-router-dom";

// eslint-disable-next-line no-unused-vars
const PopularNav = ({callback}) => {
    const [pageType1, setPageType1] = useState("all");


    const typeList = [
        {
            label: 'All',
            type: "all",
        },
        {
            label: 'Javascript',
            type: '+language:javascript',
        },
        {
            label: 'Ruby',
            type: '+language:Ruby',
        },
        {
            label: 'Java',
            type: '+language:Java',
        },
        {
            label: 'CSS',
            type: '+language:CSS',
        },
    ];

    return (
        <div style={{
            flexDirection: 'row',
            alignItems: 'center',
            display: 'flex',
            listStyle: 'none',
            fontWeight: 'bold',
        }}
        >
            {
                typeList.map((i, index) => {
                    // eslint-disable-next-line no-unused-vars
                    const navStr = `/popular/?type=${i.type}`;
                    return(
                        // eslint-disable-next-line jsx-a11y/no-static-element-interactions
                        <a onClick={() => {
                            setPageType1(i.type);
                            alert(i.type)
                            callback && callback(i.type)
                        }}
                           key={index}
                        >
                            <Link
                                style={{margin: '10px 10px', textDecoration: 'none', fontSize: '16px'}}
                                // to={{
                                //     pathname: '/popular?type=ll',
                                //     type: i.type,
                                // }}
                                to={navStr}
                                className={(pageType1 === i.type) ? 'topNavBtnSelect' : 'topNavBtn'}
                            >
                                { pageType1 === i.type ? i.label : "777"}
                            </Link>
                        </a>

                    )
                } )
            }

        </div>
    );
};
export default PopularNav;
